function tweetQuote() {
    // allows a user to semi automatically tweet the currently displayed quote
    let e = document.getElementById("quote").innerHTML;
    e = (e = e.split("<em>").join("")).split("</em>").join("");
    let t = "https://twitter.com/intent/tweet?text=" + encodeURIComponent(e);
    window.open(t)
}